import React, { useState, useEffect } from "react";
import "../App.css"; // keep your CSS import

export default function TriGasDashboard() {
  const [o2, setO2] = useState(5.7);
  const [co2, setCO2] = useState(5.4);
  const [targetO2, setTargetO2] = useState(5.5);
  const [targetCO2, setTargetCO2] = useState(5.4);
  const [alarmO2, setAlarmO2] = useState(false);
  const [alarmCO2, setAlarmCO2] = useState(false);
  const [flash, setFlash] = useState(false);
  const [showMenu, setShowMenu] = useState(false);

  useEffect(() => {
    const flashInterval = setInterval(() => {
      setFlash((f) => !f);
    }, 800);
    return () => clearInterval(flashInterval);
  }, []);

  useEffect(() => {
    setAlarmO2(Math.abs(o2 - targetO2) > 0.5);
    setAlarmCO2(Math.abs(co2 - targetCO2) > 0.5);
  }, [o2, co2, targetO2, targetCO2]);

  const cardClass = (gas, alarm) => {
    if (!alarm) return gas === "O2" ? "card green" : "card blue";
    return flash ? `card ${gas === "O2" ? "orange" : "pink"}` : `card ${gas === "O2" ? "green" : "blue"}`;
  };

  return (
    <div className="dashboard">
      <div className={cardClass("CO2", alarmCO2)}>
        <h2>% Carbon Dioxide</h2>
        <div className="reading">{co2.toFixed(1)}</div>
        <p>Target: {targetCO2}</p>
        <progress value={co2} max="10"></progress>
      </div>

      <div className={cardClass("O2", alarmO2)}>
        <h2>% Oxygen</h2>
        <div className="reading">{o2.toFixed(1)}</div>
        <p>Target: {targetO2}</p>
        <progress value={o2} max="10"></progress>

        <div className="menu-icon" onClick={() => setShowMenu(!showMenu)}>
          <span />
          <span />
          <span />
        </div>

        {showMenu && (
          <div className="menu">
            <button>Control Deadband</button>
            <button>Alarm Limits</button>
            <button>Data File Menu</button>
            <button>Password Settings</button>
          </div>
        )}
      </div>
    </div>
  );
}
