import React, { useState, useEffect } from "react";
//import { Dialog, DialogContent, DialogTrigger } from "@/components/ui/dialog";

export default function TriGasDashboard() {
  const [o2, setO2] = useState(5.7);
  const [co2, setCO2] = useState(5.4);
  const [targetO2, setTargetO2] = useState(5.5);
  const [targetCO2, setTargetCO2] = useState(5.5);
  const [alarmO2, setAlarmO2] = useState(false);
  const [alarmCO2, setAlarmCO2] = useState(false);
  const [flash, setFlash] = useState(false);

  useEffect(() => {
    const flashInterval = setInterval(() => {
      setFlash((f) => !f);
    }, 2000);
    return () => clearInterval(flashInterval);
  }, []);

  useEffect(() => {
    setAlarmO2(Math.abs(o2 - targetO2) > 0.5);
    setAlarmCO2(Math.abs(co2 - targetCO2) > 0.5);
  }, [o2, co2, targetO2, targetCO2]);

  const gasCardStyle = (gas, alarm) => {
    if (!alarm) return gas === "O2" ? "bg-green-100" : "bg-blue-100";
    return flash ? (gas === "O2" ? "bg-orange-100" : "bg-pink-100") : gas === "O2" ? "bg-green-100" : "bg-blue-100";
  };

  return (
    <div className="w-screen h-screen p-2 grid grid-cols-2">
      {/* CO2 Card */}
      <Dialog>
        <DialogTrigger asChild>
          <div className={`${gasCardStyle("CO2", alarmCO2)} flex flex-col items-center justify-center p-2 rounded-lg m-1 h-full cursor-pointer`}>
            <p className="text-md font-semibold">% Carbon Dioxide</p>
            <p className="text-5xl font-bold">{co2.toFixed(1)}</p>
          </div>
        </DialogTrigger>
        <DialogContent>
          <h2 className="text-lg font-semibold">Set Target CO₂</h2>
          <input
            type="number"
            value={targetCO2}
            onChange={(e) => setTargetCO2(parseFloat(e.target.value))}
            className="w-full border p-2 rounded"
            min={0}
            max={20}
            step={0.1}
          />
        </DialogContent>
      </Dialog>

      {/* O2 Card */}
      <Dialog>
        <DialogTrigger asChild>
          <div className={`${gasCardStyle("O2", alarmO2)} relative flex flex-col items-center justify-center p-2 rounded-lg m-1 h-full cursor-pointer`}>
            <div className="absolute top-1 right-1"><Menu /></div>
            <p className="text-md font-semibold">% Oxygen</p>
            <p className="text-5xl font-bold">{o2.toFixed(1)}</p>
          </div>
        </DialogTrigger>
        <DialogContent>
          <h2 className="text-lg font-semibold">Set Target O₂</h2>
          <input
            type="number"
            value={targetO2}
            onChange={(e) => setTargetO2(parseFloat(e.target.value))}
            className="w-full border p-2 rounded"
            min={0}
            max={21}
            step={0.1}
          />
        </DialogContent>
      </Dialog>
    </div>
  );
}

function Menu() {
  const [open, setOpen] = useState(false);
  return (
    <div>
      <button onClick={() => setOpen((o) => !o)} className="w-6 h-6 flex flex-col justify-between items-center">
        <span className="w-full h-1 bg-black" />
        <span className="w-full h-1 bg-black" />
        <span className="w-full h-1 bg-black" />
      </button>
      {open && (
        <div className="absolute top-8 right-1 bg-white border shadow-lg p-2 text-sm rounded space-y-1 z-10">
          <p>Control Deadband</p>
          <p>Alarm Limits</p>
          <p>Data File Menu</p>
          <p>Password Setting</p>
        </div>
      )}
    </div>
  );
}
