import React, { useState, useEffect } from "react";
import { Dialog, DialogContent, DialogTrigger } from "@/components/ui/dialog";

export default function TriGasDashboard() {
  const [o2, setO2] = useState(5.7);
  const [co2, setCO2] = useState(5.4);
  const [targetO2, setTargetO2] = useState(5.5);
  const [targetCO2, setTargetCO2] = useState(5.5);
  const [alarmO2, setAlarmO2] = useState(false);
  const [alarmCO2, setAlarmCO2] = useState(false);
  const [flash, setFlash] = useState(false);

  useEffect(() => {
    const flashInterval = setInterval(() => {
      setFlash((f) => !f);
    }, 2000);
    return () => clearInterval(flashInterval);
  }, []);

  useEffect(() => {
    setAlarmO2(Math.abs(o2 - targetO2) > 0.5);
    setAlarmCO2(Math.abs(co2 - targetCO2) > 0.5);
  }, [o2, co2, targetO2, targetCO2]);

  const gasCardStyle = (gas, alarm) => {
    if (!alarm) return gas === "O2" ? "bg-green-100" : "bg-blue-100";
    return flash ? (gas === "O2" ? "bg-orange-100" : "bg-pink-100") : gas === "O2" ? "bg-green-100" : "bg-blue-100";
  };

  return (
    <div className="w-screen h-screen grid grid-cols-2 gap-4 p-4 bg-gray-100">
      {/* CO2 Panel */}
      <div className="bg-blue-100 rounded-xl flex flex-col justify-center items-center shadow-lg p-6">
        <h2 className="text-xl font-bold">% Carbon Dioxide</h2>
        <p className="text-6xl font-bold mt-2">{co2.toFixed(1)}</p>
      </div>
  
      {/* O2 Panel */}
      <div className="bg-green-100 rounded-xl flex flex-col justify-center items-center shadow-lg p-6 relative">
        <h2 className="text-xl font-bold">% Oxygen</h2>
        <p className="text-6xl font-bold mt-2">{o2.toFixed(1)}</p>
  
        {/* Fake Menu Icon */}
        <div className="absolute top-4 right-4">
          <div className="w-6 space-y-1">
            <div className="h-1 bg-black"></div>
            <div className="h-1 bg-black"></div>
            <div className="h-1 bg-black"></div>
          </div>
        </div>
      </div>
    </div>
  );
  
}

function Menu() {
  const [open, setOpen] = useState(false);
  return (
    <div>
      <button onClick={() => setOpen((o) => !o)} className="w-6 h-6 flex flex-col justify-between items-center">
        <span className="w-full h-1 bg-black" />
        <span className="w-full h-1 bg-black" />
        <span className="w-full h-1 bg-black" />
      </button>
      {open && (
        <div className="absolute top-8 right-1 bg-white border shadow-lg p-2 text-sm rounded space-y-1 z-10">
          <p>Control Deadband</p>
          <p>Alarm Limits</p>
          <p>Data File Menu</p>
          <p>Password Setting</p>
        </div>
      )}
    </div>
  );
}
