import React, { useState, useEffect } from "react";
import "../App.css"; // Ensure App.css exists with required styles

export default function TriGasDashboard() {
  const [o2, setO2] = useState(5.7);
  const [co2, setCO2] = useState(5.4);
  const [targetO2, setTargetO2] = useState(5.5);
  const [targetCO2, setTargetCO2] = useState(4.1);

  const [newTargetO2, setNewTargetO2] = useState(targetO2);
  const [newTargetCO2, setNewTargetCO2] = useState(targetCO2);

  const [flash, setFlash] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);
  const [showSetpointO2, setShowSetpointO2] = useState(false);
  const [showSetpointCO2, setShowSetpointCO2] = useState(false);

  const [showDeadband, setShowDeadband] = useState(false);
  const [showAlarmLimits, setShowAlarmLimits] = useState(false);
  const [showDataFileMenu, setShowDataFileMenu] = useState(false);
  const [showPasswordSetting, setShowPasswordSetting] = useState(false);


  useEffect(() => {
    const flashInterval = setInterval(() => {
      setFlash(f => !f);
    }, 1000);
    return () => clearInterval(flashInterval);
  }, []);

  const isO2Alarm = Math.abs(o2 - targetO2) > 0.5;
  const isCO2Alarm = Math.abs(co2 - targetCO2) > 0.5;

  const getPanelClass = (alarm, base) => {
    if (!alarm) return base;
    return flash ? `${base} alarm` : base;
  };

  const handleSaveCO2 = () => {
    setTargetCO2(newTargetCO2);
    setShowSetpointCO2(false);
  };

  const handleSaveO2 = () => {
    setTargetO2(newTargetO2);
    setShowSetpointO2(false);
  };

  return (
    <div className="dashboard">
      {/* CO2 PANEL */}
      <div className={getPanelClass(isCO2Alarm, "panel co2")}>        
        <h2>% Carbon Dioxide</h2>
        <p className="value" onClick={() => setShowSetpointCO2(true)}>{co2.toFixed(1)}</p>
        <p className="target">Target: {targetCO2}</p>
        <progress max="10" value={co2}></progress>
      </div>

      {/* O2 PANEL */}
      <div className={getPanelClass(isO2Alarm, "panel o2")}>       
        <div className="menu-icon" onClick={() => setMenuOpen(!menuOpen)}>
          <span></span><span></span><span></span>
        </div>
        <h2>% Oxygen</h2>
        <p className="value" onClick={() => setShowSetpointO2(true)}>{o2.toFixed(1)}</p>
        <p className="target">Target: {targetO2}</p>
        <progress max="10" value={o2}></progress>
      </div>

      {/* MENU POPUP */}
      {menuOpen && (
        <div className="modal">
          <button onClick={() => setShowDeadband(true)}>Control Deadband</button>
          <button onClick={() => setShowAlarmLimits(true)}>Alarm Limits</button>
          <button onClick={() => setShowDataFileMenu(true)}>Data File Menu</button>
          <button onClick={() => setShowPasswordSetting(true)}>Password Setting</button>
        </div>
      )}

          {showDeadband && (
            <div className="modal">
              <h3>Control Deadband</h3>
              <p>(Placeholder for deadband control)</p>
              <button onClick={() => setShowDeadband(false)}>Close</button>
            </div>
          )}

          {showAlarmLimits && (
            <div className="modal">
              <h3>Alarm Limits</h3>
              <p>(Placeholder for alarm limit configuration)</p>
              <button onClick={() => setShowAlarmLimits(false)}>Close</button>
            </div>
          )}

          {showDataFileMenu && (
            <div className="modal">
              <h3>Data File Menu</h3>
              <p>(Placeholder for export/logging options)</p>
              <button onClick={() => setShowDataFileMenu(false)}>Close</button>
            </div>
          )}

          {showPasswordSetting && (
            <div className="modal">
              <h3>Password Setting</h3>
              <p>(Placeholder for setting or changing password)</p>
              <button onClick={() => setShowPasswordSetting(false)}>Close</button>
            </div>
          )}


      {/* Setpoint CO2 Modal */}
      {showSetpointCO2 && (
        <div className="modal">
          <h3>Set CO₂ Target</h3>
          <div className="adjust-buttons">
            <button onClick={() => setNewTargetCO2(prev => Math.max(0, parseFloat((prev - 0.1).toFixed(1))))}>−</button>
            <span>{newTargetCO2.toFixed(1)}</span>
            <button onClick={() => setNewTargetCO2(prev => parseFloat((prev + 0.1).toFixed(1)))}>+</button>
          </div>
          <button onClick={handleSaveCO2}>Save</button>
          <button onClick={() => setShowSetpointCO2(false)}>Close</button>
        </div>
      )}

      {/* Setpoint O2 Modal */}
      {showSetpointO2 && (
        <div className="modal">
          <h3>Set O₂ Target</h3>
          <div className="adjust-buttons">
            <button onClick={() => setNewTargetO2(prev => Math.max(0, parseFloat((prev - 0.1).toFixed(1))))}>−</button>
            <span>{newTargetO2.toFixed(1)}</span>
            <button onClick={() => setNewTargetO2(prev => parseFloat((prev + 0.1).toFixed(1)))}>+</button>
          </div>
          <button onClick={handleSaveO2}>Save</button>
          <button onClick={() => setShowSetpointO2(false)}>Close</button>
        </div>
      )}
    </div>
  );
}
