import React, { useState, useEffect } from "react";
import "./App.css"; // Ensure this line is included

export default function TriGasDashboard() {
  const [o2, setO2] = useState(5.7);
  const [co2, setCO2] = useState(5.4);
  const [targetO2, setTargetO2] = useState(5.5);
  const [targetCO2, setTargetCO2] = useState(4.1);
  const [alarmO2, setAlarmO2] = useState(false);
  const [alarmCO2, setAlarmCO2] = useState(false);
  const [flash, setFlash] = useState(false);
  const [showMenu, setShowMenu] = useState(false);

  useEffect(() => {
    const flashInterval = setInterval(() => {
      setFlash((f) => !f);
    }, 1000);
    return () => clearInterval(flashInterval);
  }, []);

  useEffect(() => {
    setAlarmO2(Math.abs(o2 - targetO2) > 0.5);
    setAlarmCO2(Math.abs(co2 - targetCO2) > 0.5);
  }, [o2, co2, targetO2, targetCO2]);

  const getCardClass = (gas, alarm) => {
    if (!alarm) return gas === "O2" ? "card green" : "card blue";
    return flash ? (gas === "O2" ? "card orange" : "card red") : gas === "O2" ? "card green" : "card blue";
  };

  return (
    <div className="dashboard">
      {/* CO2 Panel */}
      <div className={getCardClass("CO2", alarmCO2)}>
        <h2>% Carbon Dioxide</h2>
        <p className="value">{co2.toFixed(1)}</p>
        <p className="target">Target: {targetCO2}</p>
        <progress max="10" value={co2}></progress>
      </div>

      {/* O2 Panel */}
      <div className={getCardClass("O2", alarmO2)}>
        <h2>% Oxygen</h2>
        <p className="value">{o2.toFixed(1)}</p>
        <p className="target">Target: {targetO2}</p>
        <progress max="10" value={o2}></progress>

        {/* Simple Menu */}
        <div className="menu-button" onClick={() => setShowMenu(!showMenu)}>
          <div className="bar"></div>
          <div className="bar"></div>
          <div className="bar"></div>
        </div>

        {showMenu && (
          <div className="menu">
            <button>Control Deadband</button>
            <button>Alarm Limits</button>
            <button>Data File Menu</button>
            <button>Password Settings</button>
          </div>
        )}
      </div>
    </div>
  );
}
